import React, {useState, useMemo} from 'react'
import MyHeader from "./MyHeader"
import ContactsList from "./ContactsList"
import MyPopUp from "./UI/MyPopUp/MyPopUp"
import SearchBar from "./UI/SearchBar/SearchBar"


const ContactsBook = () => {

	const [contacts, setContacts] = useState([
		{
			id: 1,
			name: 'Lidia',
			surname: 'Nikitina',
			phone: '+38 (123) 123 45 67'
		},
		{
			id: 2,
			name: 'Alice',
			surname: 'Kluni',
			phone: '+38 (163) 123 45 67'
		},
		{
			id: 3,
			name: 'Carol',
			surname: 'Semko',
			phone: '+38 (123) 163 45 67'
		},
		{
			id: 4,
			name: 'Olga',
			surname: 'Baritska',
			phone: '+38 (123) 123 46 67'
		},
		{
			id: 5,
			name: 'Olga',
			surname: 'Marirchko',
			phone: '+38 (123) 123 45 66'
		},
		{
			id: 6,
			name: 'Svetlana',
			surname: 'Prihodko',
			phone: '+38 (323) 323 45 67'
		},
		{
			id: 7,
			name: 'Alex',
			surname: 'Manson',
			phone: '+38 (123) 123 43 67'
		}
	])
	const [currentContactId, setCurrentContactId] = useState(null)
	const [filterContacts, setFilterContacts] = useState('')

	const onContactDelete = (contact) => {
		setContacts(contacts.filter(current=>current.id !== contact))
	}

	const reducer = (state, action) => {
		switch (action.type){
			default: return state
		}
	}



	const filterHandler = useMemo (() =>{
	return contacts.filter(contact => contact.name.toLowerCase().includes(filterContacts.toLowerCase())
		|| contact.surname.toLowerCase().includes(filterContacts.toLowerCase())
		|| contact.phone.toLowerCase().includes(filterContacts.toLowerCase()))
	}, [contacts, filterContacts])


	const currentContactHandler = useMemo (() => {
		return filterHandler.find(contact=>contact.id === currentContactId)
	}, [currentContactId, filterHandler])


	const closePopUp = () => {
		setCurrentContactId(null)
	}

	const saveNewChange = (saveChangeHandler) => {
		const newContacts = contacts.slice();
		const prevIndex = newContacts.findIndex(currentId=>currentId.id === saveChangeHandler.id)
		newContacts.splice(prevIndex, 1, saveChangeHandler)
		setContacts(newContacts)
	}

	return (
		<div className={'container'}>
			<MyHeader/>
			<SearchBar
				onSearch={e=>setFilterContacts(e.target.value)}
				value={filterContacts}
			/>
			<ContactsList
				contacts={filterHandler}
				onContactDelete={onContactDelete}
				buttonChange={setCurrentContactId}
			/>

			{
				(typeof currentContactId === 'number' )
				? <MyPopUp
						currentContactHandler={currentContactHandler}
						closePopUp={closePopUp}
						saveNewChange={saveNewChange}
					/>
				: <></>
			}

		</div>
	)
}

export default ContactsBook
