import React, {useState, useMemo, useEffect} from 'react'
import MyHeader from "./MyHeader"
import ContactsList from "./ContactsList"
import MyPopUp from "./UI/MyPopUp/MyPopUp"
import SearchBar from "./UI/SearchBar/SearchBar";


const ContactsBook = () => {

	const [contacts, setContacts] = useState([
		{
			id: 1,
			name: 'Lidia',
			surname: 'Nikitina',
			phone: '+38 (123) 123 45 67'
		},
		{
			id: 2,
			name: 'Alice',
			surname: 'Kluni',
			phone: '+38 (163) 123 45 67'
		},
		{
			id: 3,
			name: 'Carol',
			surname: 'Semko',
			phone: '+38 (123) 163 45 67'
		},
		{
			id: 4,
			name: 'Olga',
			surname: 'Baritska',
			phone: '+38 (123) 123 46 67'
		},
		{
			id: 5,
			name: 'Olga',
			surname: 'Marirchko',
			phone: '+38 (123) 123 45 66'
		},
		{
			id: 6,
			name: 'Svetlana',
			surname: 'Prihodko',
			phone: '+38 (323) 323 45 67'
		},
		{
			id: 7,
			name: 'Alex',
			surname: 'Manson',
			phone: '+38 (123) 123 43 67'
		}
	])
	const [filterContact, setFilterContact] = useState('')

	const onContactDelete = (contact) => {
		setContacts(contacts.filter(current=>current.id !== contact))
	}


const filterHandler = useMemo(() => {
		return contacts.filter(contact => contact.name.toLowerCase().includes(filterContact.toLowerCase())
				|| contact.surname.toLowerCase().includes(filterContact.toLowerCase())
				|| contact.phone.toLowerCase().includes(filterContact.toLowerCase()));
		}, [contacts, filterContact]);

	return (

		<div className={'container'}>
			<MyHeader/>
			<SearchBar
				onChange={filterHandler}
				onSearch={e=>setFilterContact(e.target.value)}
			/>
			<ContactsList
				contacts={filterContact}
				onContactDelete={onContactDelete}
			/>

			<MyPopUp />
		</div>
	)
}

export default ContactsBook
