import React, {useState} from 'react'

const MyPopUp = ({currentContactHandler}) => {
	console.log(currentContactHandler);

	const [name, setName] = useState(currentContactHandler.name)
	const [surname, setSurName] = useState(currentContactHandler.surname)
	const [phone, setPhone] = useState(currentContactHandler.phone)

	const onChangeFieldsHandler = (field,value) => {
		switch (field){
			case'name': {
				setName(value);
				break;
			}
			case'secondName': {
				setSurName(value);
				break;
			}
			case'phoneNumber': {
				setPhone(value);
				break;
			}
		}
		// setSaveIndicator('unsaved');
	}


	return (
		<div>
			<div
				className="dialog_content"
			>
				<div className="dialog_header">
					<p>Edit contact: <strong>{currentContactHandler.name} {currentContactHandler.surname}</strong></p>
					<p>X</p>
				</div>
				<div className="dialog_body">
					<span></span>
					<div className="dialog_actions">
						<label htmlFor="name">First name</label>
						<input
							onChange={({target})=>onChangeFieldsHandler('name', target.value)}
							value={name}
							type="text"
						/>
						<label htmlFor="surname">Last name</label>
						<input
							onChange={({target})=>onChangeFieldsHandler('surname', target.value)}
							value={surname}
							type="text"
						/>
						<label htmlFor="phone">Phone number</label>
						<input
							onChange={({target})=>onChangeFieldsHandler('phone', target.value)}

							value={phone}
							type="text"
						/>
						<button>Save</button>
					</div>
				</div>
			</div>
		</div>
	);
};

export default MyPopUp;
