import React, {useEffect, useReducer} from 'react'

const MyPopUp = ({currentContactHandler, closePopUp, saveNewChange}) => {

	 const CONTACT_ITEM = {
		name: currentContactHandler.name,
		surname: currentContactHandler.surname,
		phone: currentContactHandler.phone,
		isSave: true
	}

	const onChangeFieldsHandler = (state, action) => {
		switch (action.type) {
			case 'name':
				return {
					...state, name: action.payload
				}
			case 'surname':
				return {
					...state, surname: action.payload
				}
			case 'phone':
				return {
					...state, phone: action.payload
				}
			case 'saveContact':
				return {
					...state, isSave: false
				}
			default: return state
		}
	}
	const [state, dispatch] = useReducer(onChangeFieldsHandler, CONTACT_ITEM);

	// const [name, setName] = useState(currentContactHandler.name)
	// const [surname, setSurName] = useState(currentContactHandler.surname)
	// const [phone, setPhone] = useState(currentContactHandler.phone)
	// const [isSave, setIsSave] = useState(true)


	useEffect(() =>{
		dispatch(
			{
				type: 'name',
				payload: state.name
			},
			{
				type: 'surname',
				payload: state.surname
			},

			{
				type: 'phone',
				payload: state.phone
			});
	}, [currentContactHandler]);


	const saveChangeHandler = () => {
	 dispatch({
		 type: 'saveContact',
		 payload: saveNewChange({
		 		id: currentContactHandler.id,
		 		name: state.name,
		 		surname: state.surname,
		 		phone: state.phone
		 	})
	 })
	}


	// const saveChangeHandler = () => {
	// if (!isSave) {
	// 	saveNewChange({
	// 		id: currentContactHandler.id,
	// 		name,
	// 		surname,
	// 		phone
	// 	})
	// }
	// 	closePopUp()
	// }

	return (
		<div>
			<div className={'my-dialog my-dialog_visible'}
				 onClick={()=>closePopUp()}
			>
				<div className={'dialog_content'}
					 onClick={(e) => e.stopPropagation()}>
					<div className="dialog_header">
						<p>Edit contact: <strong>{state.name} {state.surname}</strong></p>
						<p
							onClick={closePopUp}
						>X</p>
					</div>
					<div className="dialog_body">
						<span></span>
						<div className="dialog_actions">
							<label htmlFor="name">First name</label>
							<input
								onChange={({target})=>onChangeFieldsHandler('name', target.value)}
								value={state.name}
								type="text"
							/>
							<label htmlFor="surname">Last name</label>
							<input
								onChange={({target})=>onChangeFieldsHandler('surname', target.value)}
								value={state.surname}
								type="text"
							/>
							<label htmlFor="phone">Phone number</label>
							<input
								onChange={({target})=>onChangeFieldsHandler('phone', target.value)}

								value={state.phone}
								type="text"
							/>
							<button
								onClick={saveChangeHandler}
							>Save</button>
						</div>
					</div>
				</div>

			</div>
		</div>
	);
};

export default MyPopUp;
