const SearchBar = ({handleSearch}) => {

	const [searchQuery, setSearchQuery] = useState('')

	useEffect(() => {
		handleSearch(searchQuery)
		console.log(searchQuery);
	}, [searchQuery])

	return (
		<input
			// value={searchQuery}
			onChange={(e) => setSearchQuery(e.target.value)}
			className={'search'}
			placeholder={'Search'}
		/>
	)
}

export default SearchBar
