import React, {useEffect, useState} from 'react'

const SearchBar = ({SearchedContacts, handleSearch}) => {

	const [search, setSearch] = useState('');

	useEffect(()=>{
		handleSearch(SearchedContacts)
	}, [SearchedContacts])

	return (
		<input
			value={search}
			onChange={e=>setSearch(e.target.value)}
			className={'search'}
			placeholder={'Search'}
		/>
	)
}

export default SearchBar
